public class JwtProvider {  

    private SecretKey secretKey = Jwts.SIG.HS256.key().build();  

    //JWT ìƒì„± ë©”ì„œë“œ  
    public String create(String email){  
        Date expiredDate = Date.from(Instant.now().plus(1, ChronoUnit.HOURS)); //í˜„ì¬ì‹œê°„ì—ì„œ 1ì‹œê°„ ì¶”ê°€  

        String jwt = Jwts.builder()  
                .signWith(secretKey)        //ì•”í˜¸í™” ì•Œê³ ë¦¬ì¦˜  
                .subject(email)             //ì‚¬ìš©ì ì‹ë³„ì ê°’  
                .issuedAt(new Date())       //ìƒì„± ì‹œê°„  
                .expiration(expiredDate)    //ë§Œë£Œ ì‹œê°„  
                .compact();                 //ì••ì¶•í•˜ì—¬ ê°ì²´ ìƒì„±  

        return jwt;  
    }  

    //JWT ê²€ì¦ ë©”ì„œë“œ  
    public String validate(String jwt){  
        Claims claims = null;  

        try{  
            claims = Jwts.parser().verifyWith(secretKey)    //ì„œëª… ê²€ì¦˜  
                    .build()                                //ë¹Œë“œ  
                    .parseSignedClaims(jwt)                 //JWT íŒŒì‹±  
                    .getPayload();                          //Claims ì¶”ì¶œ  

        } catch (JwtException e){  
            e.printStackTrace();  
            return null;  
        }  

        return claims.getSubject();  
    }  
}
